import "./App.css";
import { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import ListOfBook from "./ListOfBook";
import AddAndSearch from "./AddAndSearch";
import * as BooksAPI from "./BooksAPI";

function App() {
let navigate=useNavigate();
  const bookShelfs = [
    { name: "Currently Reading", id: 1 },
    { name: "Want to Read", id: 2 },
    { name: "Read", id: 3 },
  ];

  const updateShelf=(b,s)=>{
  const newshelf=async()=>{
    const res=await BooksAPI.update(b,s);
 res[s].push(b)
  
  }

newshelf();
navigate("/");
}  
const [listBook, setListBook] = useState([]);
  const [shelves, setShelves] = useState("none");
  useEffect(() => {
    const book = async () => {
      const result = await BooksAPI.getAll();
      setListBook(result);
   
    };
    book();
    
  }, []);

  return (
    <Routes>
      <Route
        exact
        path="/"
        element={
          <ListOfBook
          bookShelfs={bookShelfs}
            listBook={listBook}
            updateShelf={updateShelf}
          />
        }
      />
      <Route path="/search" element={<AddAndSearch listBook={listBook} shelves={shelves} />} />
    </Routes>
  );
}

export default App;
