import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import { useState } from 'react';

const AddAndSearch = ({ listBook }) => {

  const [searchQuery, setSearchQuery] = useState('');
  const handleSearch = (e) => {
    setSearchQuery(e);

  }
  const searchBook = searchQuery === "" ? listBook : listBook.filter((b) => (b.title.toLowercase.includes(searchQuery)))
  return (
    <div>
      <div className="search-books">
        <div className="search-books-bar">
          <Link to="/" className="close-search">Close</Link>
          <div className="search-books-input-wrapper">
            <input
              type="text"
              placeholder="Search by title, author, or ISBN"
              value={searchQuery}
              onChange={(event) => { handleSearch(event.target.value) }} />

          </div>

        </div>

        <div className="search-books-results">
          <ol className="books-grid">{
            listBook.map((book) => (
              <li key={book.title}>
                <div className="book">
                  <div className="book-top">
                    <div
                      className="book-cover"
                      style={{
                        width: 128,
                        height: 188,
                        backgroundImage:
                          `url(${book.imageLinks.smallThumbnail})`,
                      }}
                    ></div>
                    <div className="book-shelf-changer">
                      <select>
                        <option value="none" disabled>
                          Move to...
                        </option>
                        <option value="currentlyReading">
                          Currently Reading
                        </option>
                        <option value="wantToRead">Want to Read</option>
                        <option value="read">Read</option>
                        <option value="none">None</option>
                      </select>
                    </div>
                  </div>
                  <div className="book-title">{book.title}</div>
                  <div className="book-authors">{book.authors}</div>
                </div> </li>
            ))
          }</ol>
        </div>
      </div>

    </div>
  );
}
AddAndSearch.propTypes = {
  listBook: PropTypes.array.isRequired,
}
export default AddAndSearch;
