import "./App.css";
import { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import ListOfBook from "./ListOfBook";
import AddAndSearch from "./AddAndSearch";
import * as BooksAPI from "./BooksAPI";

function App() {
  const bookShelfs = [
    { name: "Currently Reading", 
  id:1
  },
    { name: "Want to Read", 
  id:2},
    { name: "Read",
  id:3
 },
  ];
  const [listBook, setListBook] = useState([]);
  useEffect(() => {
    const book = async () => {
      const result = await BooksAPI.getAll();
      setListBook(result);
      console.log(result[0].shelf.trim()===shelf[0]);
    };
    book();
  }, []);

  return (
    <Routes>
      <Route
        exact
        path="/"
        element={bookShelfs.map((s) => (
          <ListOfBook shelf={s.name} key={s.id} listBook={listBook}/>
        ))}
      />
      <Route path="/search" element={<AddAndSearch listBook={listBook} />} />
    </Routes>
  );
}

export default App;
