import "./App.css";
import { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import ListOfBook from "./ListOfBook";
import AddAndSearch from "./AddAndSearch";
import * as BooksAPI from "./BooksAPI";

function App() {

  const bookShelfs = [
    { name: "Currently Reading", id: 1 ,shelfname:"currentlyReading"},
    { name: "Want to Read", id: 2 ,shelfname:"wantToRead"},
    { name: "Read", id: 3,shelfname:"read"},
  ];
  const handleupdate=(b,event)=>
  {

      const a=listBook.map((newb)=>{if(b.id===newb.id){
        newb.shelf=event;
      }})
setListBook(a)
      updateShelf(b, event)
    
  }  

  const updateShelf=(b,s)=>{
  const newshelf=async()=>{
    const res=await BooksAPI.update(b,s);

console.log(res)  
}

newshelf();

}  
const [listBook, setListBook] = useState([]);
  
  useEffect(() => {
    const book = async () => {
      const result = await BooksAPI.getAll();
      setListBook(result);
   
    };
    book();
    
  }, []);

  return (
    <Routes>
      <Route
        exact
        path="/"
        element={
          <ListOfBook
          bookShelfs={bookShelfs}
            listBook={listBook}
            handleupdate={handleupdate}
          />
        }
      />
      <Route path="/search" element={<AddAndSearch listBook={listBook}  />} />
    </Routes>
  );
}

export default App;
