import "./App.css";
import { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import ListOfBook from "./ListOfBook";
import AddAndSearch from "./AddAndSearch";
import * as BooksAPI from "./BooksAPI";

function App() {
  const bookShelfs=[{name:"Currently Reading"},{name:"Want to Read"},{name:"Read"}]
  const [listBook, setListBook] = useState([]);
  useEffect(() => {
    const book = async () => {
      const result = await BooksAPI.getAll();
      setListBook(result);
      console.log(result[0].id);
    };
    book();
  }, []);

  return (
    <Routes>
      <Route exact path="/" element={bookShelfs.map((s)=><ListOfBook s/>)} />
      <Route path="/search" element={<AddAndSearch listBook={listBook} />} />
    </Routes>
  );
}

export default App;
