import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import EachBook from "./EachBook";
import {useState,useEffect} from "react";
import * as BooksAPI from "./BooksAPI"
//the add button will display this page with a search bar
// when searching for a book with title or author will be shown on the page
//when select a different shelf will call the handleupdate func to update the shelf
const AddAndSearch = ({
  handleupdate,
  listBook
}) => {
  const [searches,setSearches]=useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const handleSearch = (e) => {
  e.preventDefault();
    setSearchQuery(e.target.value.trim());
    updateListSearch(e.target.value)
  }; 
if(searchQuery!==0){
  const updateListSearch = (q) => {
    const res = async()=>{
    const a=await BooksAPI.search(q);
    setSearches(listBook.concat(a))
  }
    res();
  }
}if(res.error){
  set
}
 /* useEffect(() => {
    
    const book = async () => {
      if(searchQuery!==""){
        const result = await BooksAPI.search();
      setSearches(result);}
    };
    book();
    return()=>{
    setSearches([])}
  }, []);*/
  const outputSearch=
  searches.length!==""? 
    (  <ol className="books-grid">
  {searches.filter((b)=>((b.title+b.authors.join()).toLowerCase().includes(searchQuery.toLowerCase())))
    .map((book) => (
      <EachBook
        book={book}
        key={book.title}
        handleupdate={handleupdate}
      />
    ))
    }
</ol>):(<div></div>);
  return (
    <div>
      <div className="search-books">
        <div className="search-books-bar">
          <Link to="/" className="close-search">
            Close
          </Link>
          <div className="search-books-input-wrapper">
            <input
              type="text"
              placeholder="Search by title, or author"
              value={searchQuery}
              onChange={(event) => {
                handleSearch(event);
              }}
            />
          </div>
        </div>

  
          <div className="search-books-results">
            {outputSearch}
          </div>
        
      </div>
    </div>
  );
};
AddAndSearch.propTypes = {
  listBook: PropTypes.array.isRequired,
  handleupdate: PropTypes.func.isRequired,
};
export default AddAndSearch;
