import serializeForm from "form-serialize";
import ShelfMain from "./Se"
const ListOfBook = ({ shelf, listBook,updateShelf }) => {
const handleupdate=(event)=>
{const new1=serializeForm(event.target.value)
  if(updateShelf){
    updateShelf(new1)
  }
}
  return (
    <div className="app">
      <div className="list-books">
        <div className="list-books-title">
          <h1>MyReads</h1>
        </div>
        <ShelfMain/>
        <div className="open-search">
          <Link to="/search">Add</Link>
        </div>
      </div>
    </div>
  );
};
export default ListOfBook;
