import "./App.css";
import { useState,useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import ListOfBook from "./ListOfBook";
import AddAndSearch from "./AddAndSearch";
import * as BooksAPI from "./BooksA  PI";

function App() {
const[listBook,setListBook]=useState([]);
useEffect(
()=>{
  const book=async()=>{
    const result=await BooksAPI.getAll();
setListBook(result);
  }
  book();
},[]);

  return (
    <Routes>
      <Route exact path="/" element={<ListOfBook />} />
      <Route path="/search" element={<AddAndSearch listBook={listBook}/>} />
    </Routes>
  );
}

export default App;
